import {canUseDOM} from '../execution-context';

/**
 * A hook that resolves to useEffect on the server and useLayoutEffect on the client
 * @param callback {function} Callback function that is invoked when the dependencies of the hook change
 */
export const useIsomorphicLayoutEffect = canUseDOM
  ? useLayoutEffect
  : useEffect;
import {useCallback, useRef} from 'react';

import {useIsomorphicLayoutEffect} from './useIsomorphicLayoutEffect';

export function useEvent<T extends Function>(handler: T | undefined) {
  const handlerRef = useRef<T | undefined>(handler);

  useIsomorphicLayoutEffect(() => {
    handlerRef.current = handler;
  });

  return useCallback(function (...args: any) {
    return handlerRef.current?.(...args);
  }, []);
}
import {useCallback, useRef} from 'react';

export function useInterval() {
  const intervalRef = useRef<number | null>(null);

  const set = useCallback((listener: Function, duration: number) => {
    intervalRef.current = setInterval(listener, duration);
  }, []);

  const clear = useCallback(() => {
    if (intervalRef.current !== null) {
      clearInterval(intervalRef.current);
      intervalRef.current = null;
    }
  }, []);

  return [set, clear] as const;
}
import {useRef} from 'react';

import {useIsomorphicLayoutEffect} from './useIsomorphicLayoutEffect';

export function useLatestValue<T extends any>(
  value: T,
  dependencies = [value]
) {
  const valueRef = useRef<T>(value);

  useIsomorphicLayoutEffect(() => {
    if (valueRef.current !== value) {
      valueRef.current = value;
    }
  }, dependencies);

  return valueRef;
}
import {useMemo, useRef} from 'react';

export function useLazyMemo<T>(
  callback: (prevValue: T | undefined) => T,
  dependencies: any[]
) {
  const valueRef = useRef<T>();

  return useMemo(
    () => {
      const newValue = callback(valueRef.current);
      valueRef.current = newValue;

      return newValue;
    },
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [...dependencies]
  );
}
import {useRef, useCallback} from 'react';

import {useEvent} from './useEvent';

export function useNodeRef(
  onChange?: (
    newElement: HTMLElement | null,
    previousElement: HTMLElement | null
  ) => void
) {
  const onChangeHandler = useEvent(onChange);
  const node = useRef<HTMLElement | null>(null);
  const setNodeRef = useCallback(
    (element: HTMLElement | null) => {
      if (element !== node.current) {
        onChangeHandler?.(element, node.current);
      }

      node.current = element;
    },
    //eslint-disable-next-line
    []
  );

  return [node, setNodeRef] as const;
}
import {useRef, useEffect} from 'react';

export function usePrevious<T>(value: T) {
  const ref = useRef<T>();

  useEffect(() => {
    ref.current = value;
  }, [value]);

  return ref.current;
}
import {useMemo} from 'react';

let ids: Record<string, number> = {};

export function useUniqueId(prefix: string, value?: string) {
  return useMemo(() => {
    if (value) {
      return value;
    }

    const id = ids[prefix] == null ? 0 : ids[prefix] + 1;
    ids[prefix] = id;

    return `${prefix}-${id}`;
  }, [prefix, value]);
}
function createAdjustmentFn(modifier: number) {
  return <T extends Record<U, number>, U extends string>(
    object: T,
    ...adjustments: Partial<T>[]
  ): T => {
    return adjustments.reduce<T>(
      (accumulator, adjustment) => {
        const entries = Object.entries(adjustment) as [U, number][];

        for (const [key, valueAdjustment] of entries) {
          const value = accumulator[key];

          if (value != null) {
            accumulator[key] = (value + modifier * valueAdjustment) as T[U];
          }
        }

        return accumulator;
      },
      {
        ...object,
      }
    );
  };
}

export const add = createAdjustmentFn(1);
export const subtract = createAdjustmentFn(-1);
export function hasViewportRelativeCoordinates(
  event: Event
): event is Event & Pick<PointerEvent, 'clientX' | 'clientY'> {
  return 'clientX' in event && 'clientY' in event;
}
import {getWindow} from '../execution-context';

export function isKeyboardEvent(
  event: Event | undefined | null
): event is KeyboardEvent {
  if (!event) {
    return false;
  }

  const {KeyboardEvent} = getWindow(event.target);

  return KeyboardEvent && event instanceof KeyboardEvent;
}
import {getWindow} from '../execution-context';

export function isTouchEvent(
  event: Event | undefined | null
): event is TouchEvent {
  if (!event) {
    return false;
  }

  const {TouchEvent} = getWindow(event.target);

  return TouchEvent && event instanceof TouchEvent;
}
import type {Coordinates} from './types';
import {isTouchEvent, hasViewportRelativeCoordinates} from '../event';

/**
 * Returns the normalized x and y coordinates for mouse and touch events.
 */
export function getEventCoordinates(event: Event): Coordinates | null {
  if (isTouchEvent(event)) {
    if (event.touches && event.touches.length) {
      const {clientX: x, clientY: y} = event.touches[0];

      return {
        x,
        y,
      };
    } else if (event.changedTouches && event.changedTouches.length) {
      const {clientX: x, clientY: y} = event.changedTouches[0];

      return {
        x,
        y,
      };
    }
  }

  if (hasViewportRelativeCoordinates(event)) {
    return {
      x: event.clientX,
      y: event.clientY,
    };
  }

  return null;
}
export type Transform = {
  x: number;
  y: number;
  scaleX: number;
  scaleY: number;
};

export interface Transition {
  property: string;
  easing: string;
  duration: number;
}

export const CSS = Object.freeze({
  Translate: {
    toString(transform: Transform | null) {
      if (!transform) {
        return;
      }

      const {x, y} = transform;

      return `translate3d(${x ? Math.round(x) : 0}px, ${
        y ? Math.round(y) : 0
      }px, 0)`;
    },
  },
  Scale: {
    toString(transform: Transform | null) {
      if (!transform) {
        return;
      }

      const {scaleX, scaleY} = transform;

      return `scaleX(${scaleX}) scaleY(${scaleY})`;
    },
  },
  Transform: {
    toString(transform: Transform | null) {
      if (!transform) {
        return;
      }

      return [
        CSS.Translate.toString(transform),
        CSS.Scale.toString(transform),
      ].join(' ');
    },
  },
  Transition: {
    toString({property, duration, easing}: Transition) {
      return `${property} ${duration}ms ${easing}`;
    },
  },
});
